import React, { useState, useEffect } from "react";
import { useParams } from "react-router";
import NavBar from "../NavBar/NavBar";

function ProductList() {
  const { id } = useParams(); // Get the id from the URL

  const [product, setProduct] = useState(null);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState(""); // State for search query

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5); // Set the number of items per page

  useEffect(() => {
    let url = `/api/products?id=${id}`; // Use the id to fetch product data from the API

    if (!id) {
      url = `/api/products`;
    }

    fetch(url)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setProduct(data.data); // Save the product data to state
        setFilteredProducts(data.data); // Initialize the filteredProducts state with all products
        setLoading(false); // Set loading to false once data is fetched
        console.log(data.data);
      })
      .catch((error) => {
        setError(error.message); // Set error message
        setLoading(false); // Set loading to false
      });
  }, [id]); // The useEffect hook will run whenever the id changes

  useEffect(() => {
    // Full-text search across multiple fields: name, description, and id
    if (product) {
      const filtered = product.filter((item) => {
        const lowerCaseQuery = searchQuery.toLowerCase();

        return (
          item.name.toLowerCase().includes(lowerCaseQuery) || // Match product name
          item.id.toString().includes(searchQuery) || // Match product ID
          (item.price && item.price.toLowerCase().includes(lowerCaseQuery)) // Match product description (if exists)
        );
      });
      setFilteredProducts(filtered);
    }
  }, [searchQuery, product]); // Re-run when searchQuery or product changes

  // Pagination logic
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredProducts.slice(indexOfFirstItem, indexOfLastItem);

  const totalPages = Math.ceil(filteredProducts.length / itemsPerPage);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <NavBar />

      <div className="container my-5">
        <h2 className="text-center mb-4">Product List</h2>

        {/* Search Input */}
        <div className="mb-4">
          <input
            type="text"
            className="form-control"
            placeholder="Search by Name, Description, or ID"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>

        <table className="table table-bordered table-striped">
          <thead className="thead-dark">
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Price</th>
              <th>No. of Items</th>
              <th>Discount</th>
            </tr>
          </thead>
          <tbody>
            {currentItems.map((item, index) => (
              <tr key={index}>
                <td>{item.id}</td>
                <td>{item.name}</td>
                <td>{item.price}</td>
                <td>{item.items}</td>
                <td>{item.discount}</td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Pagination Controls */}
        <div className="d-flex justify-content-center">
          <button
            className="btn btn-primary me-2"
            onClick={handlePrevPage}
            disabled={currentPage === 1}
          >
            Previous
          </button>
          <span className="align-self-center">
            Page {currentPage} of {totalPages}
          </span>
          <button
            className="btn btn-primary ms-2"
            onClick={handleNextPage}
            disabled={currentPage === totalPages}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
}

export default ProductList;
