import "./Category.css";
import React, { useState, useEffect } from "react";
import { Link } from "react-router";
import NavBar from "../NavBar/NavBar";

function Category() {
  var date = new Date().toLocaleDateString();
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const url = "api/category"; // Replace with your API URL

    fetch(url)
      .then((response) => {
        if (!response.ok) {
          console.log(response.json());
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setData(data.data); // Save the fetched data to state
        setLoading(false); // Set loading to false once data is fetched
        console.log(data);
      })
      .catch((error) => {
        setError(error.message); // Set error message
        setLoading(false); // Set loading to false
      });
  }, []); // Empty dependency array ensures it runs once

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error ho gya : {error}</p>;

  return (
    <>
      <NavBar></NavBar>
      <div id="body">

        <table border="1" style={{ width: "90%", borderCollapse: "collapse" }}>
          <thead style={{ borderCollapse: "collapse" }}>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item, index) => (
              <tr key={index}>
                <td>
                  <a href="/ProdectList:id"> {item.id} </a>
                </td>
                <td>
                <Link to={`/ProductList/${item.id}`}>{item.name}</Link>
                </td>
                <td>{date}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}

export default Category;
