import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom'; // For redirection
import NavBar from "./components/NavBar/NavBar";

function App() {
  const navigate = useNavigate();

  const [name, setName] = useState([]);

  useEffect(() => {
    // Check if the JWT token is present in localStorage
    const token = localStorage.getItem('authToken');
    if (!token) {
      // Redirect to the login page if the token is not found
      navigate('/login');
    }else{
      var data = localStorage.getItem('name');
      console.log(data);
      setName(data);
    }
  }, [navigate]); // Add navigate as a dependency to run this effect once on component mount

  return (
    <div className="App">
      <NavBar></NavBar>

      <div className="container mt-5 bg-dark text-white text-center py-5">
        <h1>Welcome to My Web Project</h1>
        <p>You Have been LoggedIn as {name}</p>
      </div>

      <div className="d-flex justify-content-center align-items-center">
      <div className="card" style={{ width: '18rem' }}>
        <img
          src="https://img.freepik.com/premium-vector/vector-flat-illustration-grayscale-avatar-user-profile-person-icon-profile-picture-business-profile-woman-suitable-social-media-profiles-icons-screensavers-as-templatex9_719432-1328.jpg" // Replace with your profile picture URL
          className="card-img-top rounded-circle mx-auto mt-3"
          alt="User Profile"
          style={{ width: '100px', height: '100px', objectFit: 'cover' }}
        />
        <div className="card-body text-center">
          <h5 className="card-title">{name}</h5>
        </div>
      </div>
    </div>

      <footer className="text-center py-4 bg-dark text-white">
        <p>
          Created as part of a learning task to master frontend and backend
          technologies.
        </p>
      </footer>
    </div>
  );
}

export default App;
