import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom"; // Use useLocation to get query parameters
import NavBar from "../NavBar/NavBar";

function Search() {
  const location = useLocation(); // Use useLocation to get query parameters
  const query = new URLSearchParams(location.search).get("q"); // Extract the "q" parameter from the URL
  const [product, setProduct] = useState(null);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5); // Set the number of items per page

  // Filter states
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(1000);
  const [minItems, setMinItems] = useState(0);
  const [minDiscount, setMinDiscount] = useState(0);

  useEffect(() => {
    let url = `/api/search?query=${query || ""}`; // Use the query to fetch product data from the API

    // Add filter parameters to the URL
    if (minPrice) url += `&minPrice=${minPrice}`;
    if (maxPrice) url += `&maxPrice=${maxPrice}`;
    if (minItems) url += `&minItems=${minItems}`;
    if (minDiscount) url += `&minDiscount=${minDiscount}`;

    fetch(url)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setProduct(data.data); // Save the product data to state
        setLoading(false); // Set loading to false once data is fetched
      })
      .catch((error) => {
        setError(error.message); // Set error message
        setLoading(false); // Set loading to false
      });
  }, [query, minPrice, maxPrice, minItems, minDiscount]); // Re-run the effect when filters change

  // Pagination logic
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = product ? product.slice(indexOfFirstItem, indexOfLastItem) : [];

  const totalPages = product ? Math.ceil(product.length / itemsPerPage) : 1;

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <NavBar />

      <div className="container my-5 bg-light p-4 rounded shadow-lg">
        <h2 className="text-center mb-4 text-primary">Product List</h2>

        {/* Filter Controls */}
        <div className="mb-4">
          <h5>Filters</h5>
          <div className="row">
            <div className="col-md-3">
              <label>Price Range</label>
              <div className="d-flex">
                <input
                  type="number"
                  className="form-control"
                  value={minPrice}
                  onChange={(e) => setMinPrice(e.target.value)}
                  placeholder="Min Price"
                />
                <span className="mx-2">-</span>
                <input
                  type="number"
                  className="form-control"
                  value={maxPrice}
                  onChange={(e) => setMaxPrice(e.target.value)}
                  placeholder="Max Price"
                />
              </div>
            </div>
            <div className="col-md-3">
              <label>Min. Items</label>
              <input
                type="number"
                className="form-control"
                value={minItems}
                onChange={(e) => setMinItems(e.target.value)}
                placeholder="Min. Items"
              />
            </div>
            <div className="col-md-3">
              <label>Min. Discount</label>
              <input
                type="number"
                className="form-control"
                value={minDiscount}
                onChange={(e) => setMinDiscount(e.target.value)}
                placeholder="Min. Discount"
              />
            </div>
          </div>
        </div>

        {/* Product Table */}
        <table className="table table-bordered table-striped table-hover">
          <thead className="thead-dark bg-primary text-white">
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Price</th>
              <th>No. of Items</th>
              <th>Discount</th>
            </tr>
          </thead>
          <tbody>
            {currentItems.map((item, index) => (
              <tr key={index}>
                <td>{item.id}</td>
                <td>{item.name}</td>
                <td>{item.price}</td>
                <td>{item.items}</td>
                <td>{item.discount}%</td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Pagination Controls */}
        <div className="d-flex justify-content-center mt-4">
          <button
            className="btn btn-primary me-2"
            onClick={handlePrevPage}
            disabled={currentPage === 1}
            style={{
              borderRadius: "5px",
              padding: "8px 16px",
            }}
          >
            <i className="bi bi-arrow-left-circle"></i> Previous
          </button>
          <span className="align-self-center text-dark">
            Page {currentPage} of {totalPages}
          </span>
          <button
            className="btn btn-primary ms-2"
            onClick={handleNextPage}
            disabled={currentPage === totalPages}
            style={{
              borderRadius: "5px",
              padding: "8px 16px",
            }}
          >
            Next <i className="bi bi-arrow-right-circle"></i>
          </button>
        </div>
      </div>
    </div>
  );
}

export default Search;
