import React, { useState, useEffect } from "react";
import { useParams } from "react-router";
import NavBar from "../NavBar/NavBar";

function Search() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState(""); // State for search query

  // Filter state
  const [priceRange, setPriceRange] = useState([0, 1000]); // Min and max price
  const [discountRange, setDiscountRange] = useState([0, 100]); // Min and max discount

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5); // Set the number of items per page

  useEffect(() => {
    let url = `/api/products?id=${id}`; // Use the id to fetch product data from the API

    if (!id) {
      url = `/api/products`;
    }

    fetch(url)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setProduct(data.data); // Save the product data to state
        setFilteredProducts(data.data); // Initialize the filteredProducts state with all products
        setLoading(false); // Set loading to false once data is fetched
        console.log(data.data);
      })
      .catch((error) => {
        setError(error.message); // Set error message
        setLoading(false); // Set loading to false
      });
  }, [id]); // The useEffect hook will run whenever the id changes

  useEffect(() => {
    // Full-text search and filter logic
    if (product) {
      const filtered = product.filter((item) => {
        const lowerCaseQuery = searchQuery.toLowerCase();

        const matchesSearchQuery =
          item.name.toLowerCase().includes(lowerCaseQuery) ||
          item.id.toString().includes(searchQuery) ||
          (item.price.toString() &&
            item.price.toString().toLowerCase().includes(lowerCaseQuery));

        const matchesPriceRange =
          item.price >= priceRange[0] && item.price <= priceRange[1];

        const matchesDiscountRange =
          item.discount >= discountRange[0] && item.discount <= discountRange[1];

        return (
          matchesSearchQuery &&
          matchesPriceRange &&
          matchesDiscountRange
        );
      });
      setFilteredProducts(filtered);
    }
  }, [searchQuery, product, priceRange, discountRange]);

  // Pagination logic
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredProducts.slice(indexOfFirstItem, indexOfLastItem);

  const totalPages = Math.ceil(filteredProducts.length / itemsPerPage);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePriceRangeChange = (e) => {
    const [min, max] = e.target.value.split('-').map(Number);
    setPriceRange([min, max]);
  };

  const handleDiscountRangeChange = (e) => {
    const [min, max] = e.target.value.split('-').map(Number);
    setDiscountRange([min, max]);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <NavBar />

      <div className="container my-5 bg-light p-4 rounded shadow-lg">
        <h2 className="text-center mb-4 text-primary">Search Result</h2>

        {/* Filter Controls */}
        <div className="mb-4">
          <div className="row">
            <div className="col-md-4">
              <label>Price Range</label>
              <select className="form-select" onChange={handlePriceRangeChange}>
                <option value="0-1000">Any Price</option>
                <option value="0-100">Under $100</option>
                <option value="100-500">$100 - $500</option>
                <option value="500-1000">$500 - $1000</option>
                <option value="1000-10000">Above $1000</option>
              </select>
            </div>
            <div className="col-md-4">
              <label>Discount Range</label>
              <select className="form-select" onChange={handleDiscountRangeChange}>
                <option value="0-100">Any Discount</option>
                <option value="0-10">0% - 10%</option>
                <option value="10-30">10% - 30%</option>
                <option value="30-50">30% - 50%</option>
                <option value="50-100">50% - 100%</option>
              </select>
            </div>
          </div>
        </div>

        <table className="table table-bordered table-striped table-hover">
          <thead className="thead-dark bg-primary text-white">
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Price</th>
              <th>No. of Items</th>
              <th>Discount</th>
            </tr>
          </thead>
          <tbody>
            {currentItems.map((item, index) => (
              <tr key={index}>
                <td>{item.id}</td>
                <td>{item.name}</td>
                <td>{item.price}</td>
                <td>{item.items}</td>
                <td>{item.discount}</td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Pagination Controls */}
        <div className="d-flex justify-content-center mt-4">
          <button
            className="btn btn-primary me-2"
            onClick={handlePrevPage}
            disabled={currentPage === 1}
            style={{
              borderRadius: "5px",
              padding: "8px 16px",
            }}
          >
            <i className="bi bi-arrow-left-circle"></i> Previous
          </button>
          <span className="align-self-center text-dark">
            Page {currentPage} of {totalPages}
          </span>
          <button
            className="btn btn-primary ms-2"
            onClick={handleNextPage}
            disabled={currentPage === totalPages}
            style={{
              borderRadius: "5px",
              padding: "8px 16px",
            }}
          >
            Next <i className="bi bi-arrow-right-circle"></i>
          </button>
        </div>
      </div>
    </div>
  );
}

export default Search;
